SELECT 

FROM test_student a




		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.major = #{major}


		AND a.scroe = #{scroe}


		AND a.birthday BETWEEN #{beginBirthday} AND #{endBirthday}




		ORDER BY ${page.orderBy}

SELECT 

FROM test_student a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE test_student SET 	
	name = #{name},
	major = #{major},
	scroe = #{scroe},
	birthday = #{birthday}
