mapper.select:
SELECT 

FROM test_teacher a

WHERE a.id = #{id}

SELECT 

FROM test_teacher a




		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.position = #{position}


		AND a.teaching = #{teaching}




		ORDER BY ${page.orderBy}

SELECT 

FROM test_teacher a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE test_teacher SET 	
	name = #{name},
	position = #{position},
	teaching = #{teaching}
WHERE id = #{id}
